
public class tabuadade7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//elabore um programa, que mostre a tabuada de multiplicacao do numero 7
		
		int valor = 1;
				
		while(valor <= 10) {
			System.out.println("7 * " + valor + " : " + (7 * valor));
			valor++;
			
		}
		
		
		
		
		
		
	}

}
